execution.script:
pipeline {
  agent any

  tools {
    maven 'maven3'
  }
  environment {
    SCANNER_HOME = tool 'sonar-scanner'
    IMAGE_NAME = "imsrushti/bankapp"
    TAG = "${params.DOCKER_TAG}" // The image tag now comes from the parameter
    KUBE_NAMESPACE = 'webapps'

  }

  parameters {
    choice(name: 'DEPLOY_ENV', choices: ['blue', 'green'], description: 'Choose which environment to deploy: Blue or Green')
    choice(name: 'DOCKER_TAG', choices: ['blue', 'green'], description: 'Choose the Docker image tag for the deployment')
    booleanParam(name: 'SWITCH_TRAFFIC', defaultValue: false, description: 'Switch traffic between Blue and Green')
  }

  stages {

    stage('Clean Workspace') {
      steps {
             cleanWs()
      }
    }  


    stage('Git Checkout') {
      steps {
        git branch: 'main', credentialsId: 'github-cred', url: 'https://github.com/SRUSHTI-AGARWAL/Blue-Green-Deployment.git'
      }
    }

    stage('Code Compile') {
      steps {
        sh 'mvn compile'
      }
    }

    stage('Unit Tests') {
      steps {
        sh 'mvn test -DskipTests=true'
      }
    }

    stage('Trivy FS Scan') {
      steps {
        sh "trivy fs --format table -o fsbg.html ."
      }
    }

    stage('Sonarqube:Code Quality Check') {
      steps {
        withSonarQubeEnv('sonar-server') {
          sh '$SCANNER_HOME/bin/sonar-scanner -Dsonar.projectName=Anything -Dsonar.projectKey=Anything -Dsonar.java.binaries=target'
        }
      }
    }

    stage("Quality Gate Check") {
      steps {
        timeout(time: 1, unit: 'HOURS') {
          waitForQualityGate abortPipeline: false
        }

      }

    }

    stage('Build Package') {
      steps {
        sh 'mvn package -DskipTests=true'
      }
    }

    stage('Publish Artifacts to Nexus') {
      steps {
        withMaven(globalMavenSettingsConfig: 'maven-settings-srushti', jdk: '', maven: 'maven3', mavenSettingsConfig: '', traceability: true) {
          sh 'mvn deploy -DskipTests=true'
        }
      }
    }

    stage('Docker Build & Tag Image') {
      steps {
        script {
          withDockerRegistry(credentialsId: 'docker-cred') {
            sh 'docker build -t ${IMAGE_NAME}:${TAG} .'
          }
        }
      }
    }

    stage('Trivy image Scan') {
      steps {
        sh 'trivy image --format table -o fsbg.html ${IMAGE_NAME}:${TAG}'
      }
    }

    stage('Docker Push Image') {
      steps {

        script {
          withDockerRegistry(credentialsId: 'docker-cred') {
            sh 'docker push ${IMAGE_NAME}:${TAG}'
          }
        }

      }
    }
    stage("Deploy MySQL Service and Deployment")
    { 
        steps{
            script{
                withKubeConfig(caCertificate: '', clusterName: 'eks-st', contextName: '', credentialsId: 'k8-token', namespace: 'webapps', restrictKubeConfigAccess: false, serverUrl: 'http://127.0.0.1:32771') {
    sh 'kubectl apply -f mysql-dbs.yaml -n ${KUBE_NAMESPACE}'
                }
            }

    }

    }

    stage('Deploy App-Service')
    { steps{
            script{
                withKubeConfig(caCertificate: '', clusterName: 'eks-st', contextName: '', credentialsId: 'k8-token', namespace: 'webapps', restrictKubeConfigAccess: false, serverUrl: 'http://127.0.0.1:32771')
                {
    sh """
         if ! kubectl get svc bankapp-service -n ${KUBE_NAMESPACE}; then 
         kubectl apply -f bankapp-service.yaml -n ${KUBE_NAMESPACE}

         fi 
         """}
        }   

    }}

    stage("Deploy Application")
    { steps{
        script {
            def deploymentFile= ""
            if ( params.DEPLOY_ENV == "blue")
              {deploymentFile = 'bankapp-blue-deployment.yaml'}
             else
             {deploymentFile = 'bankapp-green-deployment.yaml'}
             withKubeConfig(caCertificate: '', clusterName: 'eks-st', contextName: '', credentialsId: 'k8-token', namespace: 'webapps', restrictKubeConfigAccess: false, serverUrl: 'http://127.0.0.1:32771')
                {
                    sh "kubectl apply -f ${deploymentFile} -n ${KUBE_NAMESPACE}"
                    }

        }
    } }
    stage("Switch traffic between Environments") {
    when {
        expression { return params.SWITCH_TRAFFIC }
    }
    steps {
        script {
            def newEnv = params.DEPLOY_ENV
            withKubeConfig(
                caCertificate: '', 
                clusterName: 'eks-st', 
                contextName: '', 
                credentialsId: 'k8-token', 
                namespace: 'webapps', 
                restrictKubeConfigAccess: false, 
                serverUrl: 'http://127.0.0.1:32771'
            ) {
                sh """
                    kubectl patch service bankapp-service -p '{
                        "spec": {
                            "selector": {
                                "app": "bankapp",
                                "version": "${newEnv}"
                            }
                        }
                    }' -n ${KUBE_NAMESPACE}
                """
            }
            echo "Traffic has been switched to the ${newEnv} environment."
        }
    }
}


    stage('Verify Deployment') {
            steps {
                script {
                    def verifyEnv = params.DEPLOY_ENV
                    withKubeConfig(caCertificate: '', clusterName: 'devopsshack-cluster', contextName: '', credentialsId: 'k8-token', namespace: 'webapps', restrictKubeConfigAccess: false, serverUrl: 'http://127.0.0.1:32771') {
                        sh """
                        kubectl get pods -l version=${verifyEnv} -n ${KUBE_NAMESPACE}
                        kubectl get svc bankapp-service -n ${KUBE_NAMESPACE}
                        """
                    }
                }
            }
        }

  }
}
