execution.script:
pipeline {
    agent any
    tools {
        maven 'maven3'
    }
    environment{

        // creating variable for home directory of scanner tool
        SCANNER_HOME=tool 'sonar-scanner'

    }
    stages {

        stage('Clean Workspace') {
            steps {
                 cleanWs()
            }
        }
        stage('Checkout Code') {
            steps {
               git branch: 'start', credentialsId: 'github-cred', url: 'https://github.com/SRUSHTI-AGARWAL/Multi-Tier-With-Database.git'
            }
        }

        stage('Compile Code') {
            steps {
                sh 'mvn compile'
            }
        }

        stage('Run Test-cases') {
            steps {
                sh 'mvn test'
            }
        }

        stage('Trivy FS Scan ') {
            steps {
               sh  " trivy fs --format table -o cicd.html . "
            }
        }

        stage('SonarQube:Code Quality Check') {
            steps {
                withSonarQubeEnv('sonar-server') {
                 sh """ $SCANNER_HOME/bin/sonar-scanner -Dsonar.projectKey=cicd -Dsonar.projectName=cicd -Dsonar.java.binaries=target """
                }   
            }
        }

        stage('Build & Publish Artifacts to Nexus') {
            steps {
            withMaven(globalMavenSettingsConfig: 'maven-settings-srushti', jdk: '', maven: 'maven3', mavenSettingsConfig: '', traceability: true) {
                  sh "mvn deploy"   
}
            }
        }

        stage('Build & Tag Docker image') {
            steps {
                script{
                withDockerRegistry(credentialsId: 'docker-cred') {
     sh " docker build -t imsrushti/bankapp:latest ."
                 }

                }
            }
        }
        stage('Docker image scan') {
            steps {
            sh "trivy image --format  table -o cicdimage.html imsrushti/bankapp:latest"
            }
        }

        stage('Push Docker image') {
            steps {
               script{
                withDockerRegistry(credentialsId: 'docker-cred') {
                      sh " docker push imsrushti/bankapp:latest"
                 }

                }
            }
        }



        stage('Deploy Application to K8') {
            steps {

              withKubeConfig(caCertificate: '', clusterName: 'eks-st', contextName: '', credentialsId: 'k8-token', namespace: 'webapps', restrictKubeConfigAccess: false, serverUrl: 'https://ABC24B4029B58FBB9C138B4EF43B0493.gr7.ap-south-1.eks.amazonaws.com')
              {
             sh 'kubectl apply -f ds.yaml -n webapps'
             sleep 30
               } 

            }
        }

        stage('Verify Deployment') {
            steps {

              withKubeConfig(caCertificate: '', clusterName: 'eks-st', contextName: '', credentialsId: 'k8-token', namespace: 'webapps', restrictKubeConfigAccess: false, serverUrl: 'https://ABC24B4029B58FBB9C138B4EF43B0493.gr7.ap-south-1.eks.amazonaws.com')
              {
             sh 'kubectl get svc -n webapps'
               } 

            }
        }

    }
}
